import string

ciphertext1_hex = ""
ciphertext2_hex = ""

# HEX dan bytesga
ciphertext1 = bytes.fromhex(ciphertext1_hex)
ciphertext2 = bytes.fromhex(ciphertext2_hex)

# 1. Har bir belgi uchun 8-byte bloklarni ajratamiz
cipher_blocks = {}
for i, ch in enumerate(string.printable):
    start = i * 8
    end = start + 8
    cipher_blocks[ch] = ciphertext1[start:end]

# 2. Flag uchun 8-bytli bloklar
flag_blocks = [ciphertext2[i:i+8] for i in range(0, len(ciphertext2), 8)]

# 3. Har bir blok uchun mos belgi topamiz
flag = ''
for block in flag_blocks:
    for ch, enc in cipher_blocks.items():
        if block == enc:
            flag += ch
            break


flag = flag.replace('sure_', '')

print(flag)

# 5. solution.txt ga yozish
with open("solution.txt", "w") as f:
    f.write(flag)
